import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { ApiService } from '../services/api';
import './Home.css';

const Home = () => {
  const [apiStatus, setApiStatus] = useState('Loading...');
  const [motoCount, setMotoCount] = useState(0);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch API status and motorcycle count on component mount
    const fetchData = async () => {
      try {
        setLoading(true);

        // Fetch API status
        const statusData = await ApiService.getApiStatus();
        setApiStatus(statusData.message);

        // Fetch motorcycles to get count
        const motos = await ApiService.getAllMotos();
        setMotoCount(motos.length);
      } catch (error) {
        console.error('Error fetching data:', error);
        setApiStatus('Error connecting to API');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="home-container">
      <div className="hero-section">
        <h1>Bienvenido a MotoSegura</h1>
        <p className="hero-subtitle">Tu plataforma de seguridad para motocicletas</p>
        <Link to="/motorcycles" className="cta-button">Gestionar Motocicletas</Link>
      </div>

      <div className="stats-section">
        <div className="stat-card">
          <h3>Motocicletas Registradas</h3>
          <div className="stat-value">{loading ? '...' : motoCount}</div>
          <Link to="/motorcycles" className="stat-link">Ver todas</Link>
        </div>
      </div>

      <div className="features-section">
        <h2>Nuestros servicios</h2>
        <div className="features-grid">
          <div className="feature-card">
            <div className="feature-icon"></div>
            <h3>Seguridad Avanzada</h3>
            <p>Protege tu motocicleta con nuestro sistema de seguridad basado en QR</p>
          </div>
          <div className="feature-card">
            <div className="feature-icon"></div>
            <h3>App M贸vil</h3>
            <p>Accede a tu informaci贸n desde cualquier lugar con nuestra aplicaci贸n m贸vil</p>
          </div>
          <div className="feature-card">
            <div className="feature-icon"></div>
            <h3>Estad铆sticas</h3>
            <p>Visualiza estad铆sticas detalladas sobre la seguridad de tu motocicleta</p>
          </div>
        </div>
      </div>

      <div className="api-status">
        <h3>Estado del API:</h3>
        <p>{apiStatus}</p>
      </div>
    </div>
  );
};

export default Home;
