import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

// Import context
import { AuthProvider } from './context/AuthContext';

// Import components
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import { ProtectedRoute, AdminRoute } from './components/ProtectedRoute';

// Import pages
import HomePage from './pages/HomePage';
import LoginPage from './pages/LoginPage';
import FichaMedicaDetail from './pages/FichaMedicaDetail';
import AdminFichasMedicas from './pages/admin/AdminFichasMedicas';
import CreateFichaMedica from './pages/admin/CreateFichaMedica';
import EditFichaMedica from './pages/admin/EditFichaMedica';
import UnauthorizedPage from './pages/UnauthorizedPage';
import NotFoundPage from './pages/NotFoundPage';
import ProfilePage from './pages/ProfilePage';
import UserDashboard from './pages/UserDashboard';

function App() {
  return (
    <AuthProvider>
      <Router>
        <Navbar />
        <div className="container mt-3">
          <Routes>
            {/* Rutas públicas */}
            <Route path="/" element={<HomePage />} />
            <Route path="/login" element={<LoginPage />} />
            <Route path="/ficha/:id" element={<FichaMedicaDetail />} />
            <Route path="/unauthorized" element={<UnauthorizedPage />} />

            {/* Rutas protegidas para usuarios autenticados */}
            <Route element={<ProtectedRoute />}>
              <Route path="/profile" element={<ProfilePage />} />
              <Route path="/dashboard" element={<UserDashboard />} />
            </Route>

            {/* Rutas protegidas para administradores */}
            <Route element={<AdminRoute />}>
              <Route path="/admin/fichas" element={<AdminFichasMedicas />} />
              <Route path="/admin/fichas/create" element={<CreateFichaMedica />} />
              <Route path="/admin/fichas/edit/:id" element={<EditFichaMedica />} />
            </Route>

            {/* Página 404 para rutas no encontradas */}
            <Route path="*" element={<NotFoundPage />} />
          </Routes>
        </div>
        <Footer />
      </Router>
    </AuthProvider>
  );
}

export default App;
